import React from 'react';
import userService from "../services/UserService";

class UserComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            users: []
        }
        this.updateUser = this.updateUser.bind(this);
    }

    updateUser() {
        this.props.history.push('/update-user');
    }

    deleteUser() {
        this.props.history.push('/delete-user');
    }

    componentDidMount() {
        //then refers with a JS Promise
        userService.getUsers().then((response) => {
            this.setState({users: response.data})
        });
    }

    render() {
        return (
            <div>
                <h1 className="text-center">Users List</h1>
                <table className="table table-striped">
                    <thead>
                    <tr>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Telephone</th>
                        <th>Actions</th>
                    </tr>
                    </thead>

                    <tbody>
                    {
                        this.state.users.map(
                            user =>
                                <tr key={user.id}>
                                    <td>{user.username}</td>
                                    <td>{user.email}</td>
                                    <td>{user.telephone}</td>
                                    <td>
                                        <button onClick={()=>this.updateUser(user.id)} className="btn btn-outline-info">Update</button>
                                        <button onClick={()=>this.deleteUser(user.id)} className="btn btn-outline-danger">Delete</button>
                                    </td>
                                </tr>
                        )
                    }
                    </tbody>
                </table>
            </div>
        );
    }
}

export default UserComponent;
