import React, {Component} from 'react';
import BillService from "../../services/BillService";
import CommonService from "../../services/CommonService";

class AddBillComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            useridname: '',
            billamount: '',
            payment: '',
            paymentmethod: '',
            bills: [],
            item1: '',
            item2: '',
            item3: '',
            item: ''
        }
    }

    componentDidMount() {
        //then refers with a JS Promise
        CommonService.getAllBills().then((response) => {
            this.setState({bills: response.data})
        });
    }

    saveBill = (event) => {
        event.preventDefault();
        let bill = {
            useridname: this.state.useridname,
            billamount: this.state.billamount,
            payment: this.state.createdtime,
            paymentmethod: this.state.createdtime,
            item1: this.state.item1,
            item2: this.state.item2,
            item3: this.state.item3
        };
        console.log('Bill Details => ' + JSON.stringify(bill));

        BillService.addBill(bill).then(res => {
            this.props.history.push('/bills');
        });
    }

    changeNamehandler = (event) => {
        this.setState({name: event.target.value});
    }
    changeTelephonehandler = (event) => {
        this.setState({telephone: event.target.value});
    }
    changeItem1Handler = (event) => {
        // this.setState({item1: event.target.value});
    }
    changeItem2Handler = (event) => {
        // this.setState({item2: event.target.value});
    }
    changeItem3Handler = (event) => {
        // this.setState({item3: event.target.value});
    }
    changeItem4Handler = (event) => {
        // this.setState({item3: event.target.value});
    }

    handleChange(field, e) {
        let fields = this.state.fields;
        fields[field] = e.target.value;
        this.setState({fields});
    }

    cancel() {
        this.props.history.push('/suppliers');
    }

    render() {
        const spaceBottom = {
            paddingBottom: "50px",
            paddingTop: "25px"
        };
        return (
            <div className="container" style={spaceBottom}>
                <div className="row">
                    <div className="card col-md-6 offset-md-3">
                        <h3 className="text-center">New Bill Page</h3>
                        <div className="card-body">
                            <form action="signup">
                                <div className="form-group">
                                    <label>User Name </label>
                                    <input type="text" placeholder="Supplier Name" name="name"
                                           className="form-control"
                                           value={this.state.useridname} onChange={this.changeNamehandler}/>
                                </div>
                                <div className="form-group">
                                    <label>Items</label><br/>
                                    <select value={this.state.item} onChange={this.changeItem1Handler}>
                                        {this.state.items.map((data) => (
                                            <option value={data.id}>{data.name}</option>
                                        ))}
                                    </select>
                                    <select value={this.state.item} onChange={this.changeItem2Handler} style={{marginLeft: "10px"}}>
                                        {this.state.items.map((data) => (
                                            <option value={data.id}>{data.name}</option>
                                        ))}
                                    </select>
                                    <select value={this.state.item} onChange={this.changeItem3Handler} style={{marginLeft: "10px"}}>
                                        {this.state.items.map((data) => (
                                            <option value={data.id}>{data.name}</option>
                                        ))}
                                    </select>
                                    <select value={this.state.item} onChange={this.changeItem4Handler} style={{marginLeft: "10px"}}>
                                        {this.state.items.map((data) => (
                                            <option value={data.id}>{data.name}</option>
                                        ))}
                                    </select>
                                </div>
                                <div className="form-group">
                                    <label>BillAmount </label>
                                    <input type="text" placeholder="BillAmount/LKR" name="billamount"
                                           className="form-control"
                                           value={this.state.item1} onChange={this.changeItem1handler}/>
                                </div>
                                <div className="form-group">
                                    <label>Payment </label>
                                    <input type="text" placeholder="Payment/LKR" name="Payment"
                                           className="form-control"
                                           value={this.state.item2} onChange={this.changeItem2handler}/>
                                </div>
                                <div className="form-group">
                                    <label>Payment Type </label>
                                    <input type="text" placeholder="Payment Type" name="paymenttype"
                                           className="form-control"
                                           value={this.state.item3} onChange={this.changeItem3handler}/>
                                </div>
                                <div className="form-group">
                                    <label>Items</label><br/>
                                    <select value={this.state.item} onChange={this.changeItemsHandler}>
                                        {this.state.items.map((data) => (
                                            <option value={data.id}>{data.name}</option>
                                        ))}
                                    </select>
                                </div>
                                <button className="Btn btn-success" onClick={this.saveSupplier}>Save</button>
                                <button className="Btn btn-danger" onClick={this.cancel.bind(this)}
                                        style={{marginLeft: "10px"}}>Cancel
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default AddBillComponent;
