import React, {Component} from 'react';
import StockService from "../../services/StockService";
import SupplierService from "../../services/SupplierService";
import Select from 'react-select';

class AddStockComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            itemcode: '',
            suppliers: [],
            amountremaining: '',
            unitprice: '',
            selectedOption: '',
            supplier: ''
        }
    }

    saveStock = (event) => {
        event.preventDefault();
        let stock = {
            name: this.state.name,
            itemcode: this.state.itemcode,
            supplier: this.state.supplier,
            amountremaining: this.state.amountremaining,
            unitprice: this.state.unitprice
        };
        console.log('Stock => ' + JSON.stringify(stock));

        StockService.addStock(stock).then(res => {
            this.props.history.push('/stocks');
        });
    }

    componentDidMount() {
        //then refers with a JS Promise
        SupplierService.getSuppliers().then((res) => {
                this.setState({
                    suppliers: res.data
                })
                console.log(this.state.suppliers[0].name);
            }
        );
    }

    changeNameHandler = (event) => {
        this.setState({name: event.target.value});
    }
    changeItemCodeHandler = (event) => {
        this.setState({itemcode: event.target.value});
    }
    changeSupplierHandler = (event) => {
        this.setState({supplier: event.target.value});
    }
    changeAmountremainingHandler = (event) => {
        this.setState({amountremaining: event.target.value});
    }
    changeUnitPriceHandler = (event) => {
        this.setState({unitprice: event.target.value});
    }

    handleValidation() {
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Name
        if (!fields["name"]) {
            formIsValid = false;
            errors["name"] = "Cannot be empty";
        }

        if (typeof fields["name"] !== "undefined") {
            if (!fields["name"].match(/^[a-zA-Z]+$/)) {
                formIsValid = false;
                errors["name"] = "Only letters";
            }
        }

        this.setState({errors: errors});
        return formIsValid;
    }

    contactSubmit(e) {
        e.preventDefault();

        if (this.handleValidation()) {
            alert("Form submitted");
        } else {
            alert("Form has errors.")
        }

    }

    handleChange(field, e) {
        let fields = this.state.fields;
        fields[field] = e.target.value;
        this.setState({fields});
    }

    cancel() {
        this.props.history.push('/stocks');
    }

    render() {

        // let options = this.state.suppliers.map(function (supplier) {
        //     console.log(supplier.name + " == " + supplier.id);
        //     return {value: supplier.name, label: supplier.id};
        // })
        renderList()
        {
            return (this.state.suppliers.map(data => ({label: data.name, value: data.id})))
        }

        const mystyle = {
            paddingBottom: "50px"
        };
        return (
            <div className="container" style={mystyle}>
                <div className="row">
                    <div className="card col-md-6 offset-md-3">
                        <h3 className="text-center">Stock Registration Page</h3>
                        <div className="card-body">
                            <form action="signup">
                                <div className="form-group">
                                    <label>Item Name </label>
                                    <input type="text" placeholder="Item Name" name="name"
                                           className="form-control"
                                           value={this.state.name} onChange={this.changeNameHandler}/>
                                </div>
                                <div className="form-group">
                                    <label>Item Code </label>
                                    <input type="text" placeholder="Item Code" name="itemcode"
                                           className="form-control"
                                           value={this.state.itemcode} onChange={this.changeItemCodeHandler}/>
                                </div>
                                <div className="form-group">
                                    <label>Supplier</label><br/>
                                    <Select
                                        name="form-field-name"
                                        value={this.state.value}
                                        labelKey='name'
                                        valueKey='id'
                                        options={this.state.suppliers}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Amount Remaining </label>
                                    <input type="text" placeholder="Amount Remaining" name="amountremaining"
                                           className="form-control"
                                           value={this.state.amountremaining}
                                           onChange={this.changeAmountremainingHandler}/>
                                </div>
                                <div className="form-group">
                                    <label>Unit Price </label>
                                    <input type="text" placeholder="Unit Price" name="unitprice"
                                           className="form-control"
                                           value={this.state.unitprice} onChange={this.changeUnitPriceHandler}/>
                                </div>
                                <button className="Btn btn-success" onClick={this.saveStock}>Save</button>
                                <button className="Btn btn-danger" onClick={this.cancel.bind(this)}
                                        style={{marginLeft: "10px"}}>Cancel
                                </button>

                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default AddStockComponent;
